// Status bar text scroller
let statusText = "ðŸš€ Welcome to the FUTURE! | Made with JavaScript | Running on Windows 95 | ";
let statusIndex = 0;

setInterval(() => {
    window.status = statusText.substring(statusIndex) + statusText.substring(0, statusIndex);
    statusIndex = (statusIndex + 1) % statusText.length;
}, 100);

// Cursor trail effect
const trailContainer = document.getElementById('cursor-trail');
const trailElements = [];
const TRAIL_LENGTH = 10;

// Create trail elements
for (let i = 0; i < TRAIL_LENGTH; i++) {
    const trail = document.createElement('div');
    trail.innerHTML = "â˜…";
    trail.style.cssText = `
        position: absolute;
        font-size: ${20 - i}px;
        color: hsl(${i * 36}, 100%, 50%);
        transition: all 0.1s ease;
        opacity: ${1 - (i / TRAIL_LENGTH)};
    `;
    trailContainer.appendChild(trail);
    trailElements.push(trail);
}

// Update trail positions
document.addEventListener('mousemove', (e) => {
    setTimeout(() => {
        trailElements.forEach((trail, index) => {
            setTimeout(() => {
                trail.style.left = (e.pageX - 10) + 'px';
                trail.style.top = (e.pageY - 10) + 'px';
            }, index * 50);
        });
    }, 0);
});

// Random color change for buttons
const buttons = document.querySelectorAll('.button');
setInterval(() => {
    buttons.forEach(button => {
        button.style.background = `#${Math.floor(Math.random()*16777215).toString(16)}`;
    });
}, 1000);

// Matrix rain effect
const canvas = document.getElementById('matrix');
const ctx = canvas.getContext('2d');

canvas.width = window.innerWidth;
canvas.height = window.innerHeight;

const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ123456789@#$%^&*()*&^%";
const fontSize = 14;
const columns = canvas.width/fontSize;
const drops = [];

for(let x = 0; x < columns; x++)
    drops[x] = 1; 

function draw() {
    ctx.fillStyle = 'rgba(0, 0, 0, 0.05)';
    ctx.fillRect(0, 0, canvas.width, canvas.height);
    
    ctx.fillStyle = '#0F0';
    ctx.font = fontSize + 'px monospace';

    for(let i = 0; i < drops.length; i++) {
        const text = letters[Math.floor(Math.random() * letters.length)];
        ctx.fillText(text, i*fontSize, drops[i]*fontSize);
        
        if(drops[i]*fontSize > canvas.height && Math.random() > 0.975)
            drops[i] = 0;
        
        drops[i]++;
    }
}

setInterval(draw, 33);

